import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { AuthService } from '../auth.service';

@Component({
  selector: 'pm-categories',
  templateUrl: './categories.component.html',
  styleUrls: ['./categories.component.css']
})
export class CategoriesComponent implements OnInit {

  categories:any[]=[];
  idSelected:number|null=null;
  nameSelected:string='';
  courses:any[]=[];

  constructor(private http: HttpClient, private authService:AuthService){}

  ngOnInit(): void{
    const token=this.authService.getToken();
    const headers=new HttpHeaders({'Content-Type':'application/json','Authorization':`Bearer ${token}`})
    //const headers=new HttpHeaders().set('Authorizaton',`Bearer ${token}`);
    console.log("category");
    console.log(token);
    console.log(headers);
    this.http.get('http://127.0.0.1:8000/api/category',{headers}).subscribe((data:any)=>{
    this.categories=data;
    console.log("inside get");
  });
}

  onChange(): void{
    if(this.idSelected!==null)
    {
      const categoryId= +this.idSelected;
      const url=`http://127.0.0.1:8000/api/category/${categoryId}`;

      const token=this.authService.getToken();
      const headers=new HttpHeaders({'Content-Type':'application/json','Authorization':`Bearer ${token}`})
      //const headers=new HttpHeaders().set('Authorizaton',`Bearer ${token}`);

      this.http.get(url,{headers}).subscribe((data:any)=>{
        this.courses=data;
  });

    const categorySelected=this.categories.find(category=>category.id===categoryId);
    this.nameSelected=categorySelected ? categorySelected.name: '';
  }
  else{
    this.courses=[];
    this.nameSelected='';
  }
  }


}
<div class="card">
    <div class="card-header">
      Categories
    </div>

<div class="container mt-4">
  <div class="row">
    <div class="col-md-4">
      <!--dropdown-->
      <div class="form-group row mb-4">
        <label for="selectCategory" class="text-primary">Select Category</label>
        <div class="col_md-4">
          <select class="form-control custom-select" id="selectCategory" [(ngModel)]="idSelected" (change)="onChange()">
            <option [ngValue]="null">Select Category</option>
            <option *ngFor="let category of categories" [value]="category.id">{{category.name}}</option>
          </select>
        </div>
      </div>
    </div>
  </div>
  <div class="row">
    <div class="col-md-12">
      <div class="col-md-6">
        <h4>Courses in {{nameSelected}}</h4>
      </div>

  <div class="table-responsive">
    <table class="table mb-0">
      <thead>
        <tr>
          <th scope="col">Name</th>
          <th scope="col">Code</th>
          <th scope="col">Price</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let course of courses">
          <td>{{course.CourseName}}</td>
          <td>{{course.CourseCode}}</td>
          <td>{{course.price | currency:'USD':true:'1.2-2'}}</td>
        </tr>
      </tbody>
    </table>
  </div>
  </div>
  </div>
</div>
</div>
